mapper.select:
select a.admin_id,a.device_id,a.device_sn,a.device_name,a.last_connected,a.device_online from gywlw_device a,(SELECT device_id FROM `gywlw_project_device_group` JOIN gywlw_project_user_group ON gywlw_project_device_group.project_id=gywlw_project_user_group.project_id where gywlw_project_user_group.user_id = #{userId,jdbcType=VARCHAR}) t
where a.device_id = t.device_id ;
where a.device_id = t.device_id AND a.device_sn = {deviceSn,jdbcType=VARCHAR};

select
admin_id,device_id,device_sn,device_name,last_connected,device_online
from gywlw_device

  where admin_id = #{userId,jdbcType=VARCHAR};


  where admin_id = #{userId,jdbcType=VARCHAR} AND device_sn = {deviceSn,jdbcType=VARCHAR};

select a.* from gywlw_device a

	where a.factory_id=#{factoryId}


	LEFT JOIN gywlw_user_admin_group b on b.admin_id=a.admin_id
	where b.user_id=#{factoryId}


	and a.device_sn like CONCAT('%','${deviceSn}','%')
