mapper.select:
select

from gywlw_history_data_for_gpio
where device_id = #{deviceId,jdbcType=VARCHAR} and gpio_id = #{gpioId,jdbcType=VARCHAR}

SELECT * from (select a.*,c.variable_name from gywlw_history_data_for_gpio a,gywlw_variable_reg_group b,gywlw_variable c where c.project_id = #{projectId,jdbcType=VARCHAR} and b.variable_id = c.variable_id
and c.variable_name = #{variableName,jdbcType=VARCHAR}
and a.gpio_id = b.reg_id ORDER BY time desc) t GROUP BY gpio_id

mapper.insert:
insert into gywlw_history_data_for_gpio (gpio_id, value, time, device_id, content, alarm
)
values

  (#{a.gpioId,jdbcType=VARCHAR},#{a.value,jdbcType=INTEGER},
  #{a.time,jdbcType=TIMESTAMP},#{a.deviceId,jdbcType=VARCHAR},#{a.content,jdbcType=VARCHAR},
  #{a.alarm,jdbcType=VARCHAR}
  )
