mapper.select:
select
a.item_id,a.reg_id,a.plc_name,a.device_name,a.item_type,a.item_content,a.item_time,a.variable_name,a.severity,a.item_name,a.item_alias,a.rule_id,b.rule_name,b.operator,b.operand
from gywlw_history_item a,gywlw_warning_rules b

where a.item_time BETWEEN #{startTime,jdbcType=TIMESTAMP} AND #{endTime,jdbcType=TIMESTAMP} and a.rule_id = b.rule_id and a.variable_name = #{variableName,jdbcType=VARCHAR} AND a.project_id = #{projectId,jdbcType=VARCHAR}


where a.item_time BETWEEN #{startTime,jdbcType=TIMESTAMP} AND #{endTime,jdbcType=TIMESTAMP} and a.rule_id = b.rule_id AND a.project_id = #{projectId,jdbcType=VARCHAR}

ORDER BY a.item_time DESC ;

		SELECT item_id,device_name,item_name,item_alias,item_type,item_value,item_time,variable_name,severity,item_content
     from gywlw_history_item where device_id=#{deviceId}
		and item_time between #{startTime} and #{endTime}
     and severity in (1,2)
     ORDER BY item_time DESC ;

SELECT item_time,item_value
from gywlw_history_item
where reg_id=#{regId}
and item_time between #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
ORDER BY item_time DESC ;
