mapper.select:
select a.id,a.variable_id,b.variable_name,a.reg_id,c.reg_name,a.device_id,d.device_name,a.project_id,d.last_connected from gywlw_variable_reg_group a,gywlw_variable b,gywlw_reg_info c,gywlw_device d, gywlw_project e
where a.project_id = #{projectId,jdbcType=VARCHAR} AND a.device_id = d.device_id and a.project_id = e.project_id and a.reg_id = c.reg_id and a.variable_id = b.variable_id
where a.project_id = #{projectId,jdbcType=VARCHAR} AND a.device_id = d.device_id and a.project_id = e.project_id and a.reg_id = c.reg_id and a.variable_id = b.variable_id and b.variable_name = #{variableName,jdbcType=VARCHAR};

